var makelove = {

        debug: false,

        endpoint: null,

        log: function (what) {
            if (makelove.debug) {
                console.log(what);
            }
        },

        exists: function (el) {
            if ($(el).length > 0) {
                return true;
            }
        }, 

        init: function () {

            if (this.exists('#owl-demo')){
                hero.init($('#owl-demo'));
            }

            // if( this.exists('.cart')){
            //     cartgrand.init($('.cart'))
            // }

        }

    } || {};

(function ($, window, undefined) {
    'use strict';

    $(document).ready(function () {
        makelove.init();
    });

})(jQuery, this);

var hero = {
	init: function($el){
		this.$el = $el;
		this.startOwl();
	},

	startOwl: function(){
		this.$el.owlCarousel({
			navigation : true,
			singleItem : true,
			navigationText : false,
			mouseDrag :  false,
			autoPlay :   3000,
			stopOnHover : true,
			transitionStyle : "goDown"
		});
	}
};
